defined('BASEPATH') OR exit('No direct script access allowed');

class Htmleditor extends CI_Controller
{
    public function __construct()
	{
		parent::__construct();
		$this->user->login_redirect();
		if ( $this->user->level <= 2) // permission denied
			show_404();

		$this->autosave = $this->settings_model->get_setting('assignments_root') . "/htmleditor.auto.save.txt";
	}
    public function index(){

		
		$content = '';
		if (file_exists($this->autosave)) {
			$content = file_get_contents($this->autosave);
		}
		if ($content == "")  
		$content = '<p>Just click on the text to start editing. You can insert formula <span class="math-tex">\(x = {-b \pm \sqrt{b^2-4ac} \over 2a}\)</span>or image&nbsp;&nbsp;<img alt="" src=" data:image/svg+xml;base64," style="height:55px; width:60px" /> as well.</p>';
	
		file_put_contents($this->autosave, $content);

		//var_dump($content); die();
        $this->twig->display('pages/htmleditor.twig', array('content' => $content));
	}
	
	public function autosave(){
		$this->form_validation->set_rules('content', 'content', 'required');

		if ($this->form_validation->run())
		{
			$content = $this->input->post("content");
			$a =file_put_contents($this->autosave, $content); 
			if ($a){
				
				echo "success";
				return;
			}
		}

		echo "shit";
		show_error("saving fail", 403);
	}
}

?>
